package grapefruit.command.annotation.argument;

import java.lang.annotation.ElementType;
import java.lang.annotation.Retention;
import java.lang.annotation.RetentionPolicy;
import java.lang.annotation.Target;

/**
 * Marks a parameter of a command handler method to be a
 * command argument. A corresponding {@link grapefruit.command.argument.CommandArgument}
 * will be generated by the code generation module.
 *
 * @see grapefruit.command.argument.CommandArgument
 */
@Retention(RetentionPolicy.SOURCE)
@Target(ElementType.PARAMETER)
public @interface Arg {

    /**
     * The name of the argument. By default, the parameter name is used.
     */
    String name() default "";
}
